svg.script:
function f() {
  var svg = document.documentElement;
  svg.suspendRedraw(10000);
  setTimeout(function() {
    document.getElementById("r").setAttribute("requiredFeatures", "http://www.w3.org/TR/SVG11/feature#Gradient");
    svg.unsuspendRedrawAll();
    setTimeout(function() {
      svg.removeAttribute("class");
    }, 1);
  }, 1);
}
