if (window.layoutTestController) layoutTestController.dumpAsText(); document.execCommand("SelectAll"); range = document.createRange(); range.setStartBefore(document.getElementById("stop")); range.insertNode(document.getElementById("mask"));
/* What does this do? It moves the whole <mask> element as child of the <linearGradient>. The final DOM looks like:
<g> <linearGradient> <mask id="mask"> <g filter="url(#filter)"> <linearGradient id="gradient"/> <path fill="url(#gradient)" d="..."/> </g> </mask> <stop id="stop"/> </linearGradient> </g> <g> <defs> <filter id="filter"/> </defs> <path mask="url(#mask)" d="...."/> </g> */
