svg.script:


 if (window.testRunner)
     testRunner.waitUntilDone();

function changeFill(evt) { 
    var poly = evt.target;
    poly.setAttribute("fill", "green");
}

function click() { 
     if (window.eventSender) {
         // Fill click
         eventSender.mouseMoveTo(25, 25);
         eventSender.mouseDown();
         eventSender.mouseUp();
     }
 }

 if (window.testRunner)
   testRunner.notifyDone();
