svg.script:
function runTest() {
  if (window.testRunner)
      testRunner.waitUntilDone();
   eventSender.mouseMoveTo(100, 50);
   eventSender.mouseDown();
   eventSender.mouseUp();
}
function reset() {
  var u = document.querySelector('#use1');
  u.setAttribute('x', 0);
  u.setAttribute('y', 0);
  if (window.testRunner)
      testRunner.notifyDone();
}
